<?php

if (!empty($_FILES)) {

    $fileGroup = current($_FILES);
    $patchNames = [];
    foreach ($fileGroup['name'] as $name) {
        $patchNames[] = preg_replace('/\.(patch|txt|diff)/', '', $name);
    }

    $diff = '';
    foreach ($fileGroup['tmp_name'] as $p) {
        $diff .= file_get_contents($p);
    }

    $diffParts = preg_split('/\ndiff/', $diff);

    $diffArray = [];
    $diffRectified = [];
    $newFiles = [];
    $existingFiles = [];

    foreach ($diffParts as $dp) {
        preg_match('/\-\-git\s([^\s]*)\s([^\s]*)\n/', $dp, $fileNames);

        $a = $fileNames[1];
        $b = $fileNames[2];

//    $dp = preg_replace('/index\s[^\n]*\n/', '', $dp);

        $diffSingle = [
            'a' => $a,
            'b' => $b,
            'diff' => (preg_match('/^diff/', $dp) ? '' : 'diff') . $dp
        ];

        $diffArray[] = $diffSingle;

        if (!preg_match('/(dev\/tests|Test|composer\.lock|composer\.json)/', $a)) {
            $diffRectified[] = $diffSingle;

            if (preg_match('/(\nnew\sfile\smode)/', $dp)) {
                $newFiles[] = preg_replace('/^a\//', '', $a);
            } else {
                $existingFiles[] = preg_replace('/^a\//', '', $a);
            }
        }
    }

    $diffRectifiedPatch = '';
    if ($diffRectified) {
        foreach ($diffRectified as $d) {
            $diffRectifiedPatch .= $d['diff'] . "\n";
        }
    }

    $filename = implode('_', $patchNames) . '_rectified.patch';

    if (!file_exists('./var')) {
        mkdir('./var', 0777);
    }
    
    $diffFileData = "data:text/x-diff;base64," . base64_encode($diffRectifiedPatch);
}

$image = 'data:image/jpeg;base64,';

?>
